import React, { useState } from 'react';
import ProductList from './components/ProductList';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faShoppingCart } from '@fortawesome/free-solid-svg-icons';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);

  const handleLoginLogoutClick = () => {
    if (isLoggedIn) {
      // Perform logout logic
      setIsLoggedIn(false);
    } else {
      // Show login dropdown
      setShowDropdown(!showDropdown);
    }
  };

  return (
    <div className="App">
      {/* ...other code remains unchanged */}
      <div className="flex items-center">
        <FontAwesomeIcon icon={faShoppingCart} className="text-xl mr-6 cursor-pointer" />
        <div className="relative">
          <FontAwesomeIcon icon={faUser} className="text-xl cursor-pointer" onClick={handleLoginLogoutClick} />
          {showDropdown && (
            <div className="absolute right-0 mt-2 py-2 w-48 bg-white rounded-md shadow-xl z-20">
              {!isLoggedIn ? (
                <button onClick={() => setIsLoggedIn(true)} className="w-full text-left block px-4 py-2 text-sm capitalize text-gray-700 hover:bg-blue-500 hover:text-white">
                  Login
                </button>
              ) : (
                <>
                  {/* Logged in user dropdown items */}
                  <a href="#" className="block px-4 py-2 text-sm capitalize text-gray-700 hover:bg-blue-500 hover:text-white">
                    Profile
                  </a>
                  <a href="#" className="block px-4 py-2 text-sm capitalize text-gray-700 hover:bg-blue-500 hover:text-white">
                    Settings
                  </a>
                  <button onClick={() => setIsLoggedIn(false)} className="w-full text-left block px-4 py-2 text-sm capitalize text-gray-700 hover:bg-blue-500 hover:text-white">
                    Logout
                  </button>
                </>
              )}
            </div>
          )}
        </div>
      </div>
      {/* ... */}
    </div>
  );
}

export default App;
