import React, { useState, useRef, useEffect } from 'react';
import ProductCard from './ProductCard';
import products from '../Products'; // Adjust the import path as necessary



const ProductList = () => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    console.log('Cart Items:', cartItems);
  }, [cartItems]);  

const handleAddToCart = (product) => {
  // Check if the product is already in the cart
  const isProductInCart = cartItems.some(item => item.id === product.id);

  if (!isProductInCart) {
    setCartItems(prevItems => {
      const updatedCartItems = [...prevItems, product];
      console.log('Updated Cart Items:', updatedCartItems); // Added log
      return updatedCartItems;
    });
  } else {
    console.log('Product is already in cart'); // Added log
  }
};


  return (
    <div className="container mx-auto px-4 py-6">
      <div className="grid grid-cols-1 gap-6">
        {products.map((product) => (
          <ProductCard key={product.id} {...product} onAddToCart={() => handleAddToCart(product)} />
        ))}
      </div>
    </div>
  );
};

export default ProductList;
