import React, { useState, useEffect, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import ProductList from './components/ProductList';
import { useNavigate } from 'react-router-dom';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [cartVisible, setCartVisible] = useState(false);
  const [cartItems, setCartItems] = useState([]);
  const [showUserDropdown, setShowUserDropdown] = useState(false);
  const userDropdownRef = useRef(null);
  const cartDropdownRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    function handleClickOutside(event) {
      if (userDropdownRef.current && !userDropdownRef.current.contains(event.target)) {
        setShowUserDropdown(false);
      }
      if (cartDropdownRef.current && !cartDropdownRef.current.contains(event.target)) {
        setCartVisible(false);
      }
    }

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleUserIconClick = () => {
    setShowUserDropdown(!showUserDropdown);
  };

  const handleCartIconClick = () => {
    setCartVisible(!cartVisible);
  };

  const handleLogin = () => {
    navigate('/login');
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setShowUserDropdown(false);
  };

  const handleCheckout = () => {
    navigate('/checkout'); // Assuming '/checkout' is your checkout page route
  };

  const handleAddToCart = (product) => {
    setCartItems(prevItems => {
      const isProductInCart = prevItems.find(item => item.id === product.id);
      if (!isProductInCart) {
        return [...prevItems, product];
      }
      return prevItems;
    });
  };

  const handleRemoveFromCart = (productId) => {
    setCartItems(prevItems => prevItems.filter(item => item.id !== productId));
  };


  return (
    <div className="App">
      <div className="bg-gradient-to-r from-red-500 to-yellow-500 to-green-500 to-blue-500 to-indigo-500 to-purple-500 h-1"></div>
      <header className="bg-white py-4 flex justify-between items-center">
        <div className="container mx-auto px-4 flex justify-between items-center">
          <h1 className="text-3xl font-bold text-red-600">blocket</h1>
          <div className="flex items-center">
            <div className="relative mr-4" ref={cartDropdownRef}>
              <FontAwesomeIcon
                icon={faShoppingCart}
                className={`text-xl cursor-pointer ${cartItems.length > 0 ? 'text-green-600' : 'text-gray-400'}`}
                onClick={handleCartIconClick}
              />
              {cartItems.length > 0 && (
                <span className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full h-6 w-6 flex items-center justify-center text-xs">
                  {cartItems.length}
                </span>
              )}
              {cartVisible && (
                <div className="absolute right-0 mt-2 w-64 bg-white rounded-md shadow-xl z-20 p-4">
                  {cartItems.length === 0 ? (
                    <div className="text-center text-gray-500">Your cart is empty.</div>
                  ) : (
                    <ul className="mb-4">
                      {cartItems.map((item) => (
                        <li key={item.id} className="flex justify-between items-center mb-2">
                          <span>{item.name}</span>
                          <button
                            onClick={() => handleRemoveFromCart(item.id)}
                            className="text-red-600 hover:text-red-800"
                          >
                            &times;
                          </button>
                        </li>
                      ))}
                    </ul>
                  )}
                  <button
                    onClick={handleCheckout}
                    className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
                    disabled={cartItems.length === 0}
                  >
                    Checkout
                  </button>
                </div>
              )}
            </div>
            <div className="relative" ref={userDropdownRef}>
              <FontAwesomeIcon icon={faUser} className="text-xl cursor-pointer" onClick={handleUserIconClick} />
              {showUserDropdown && (
                <div className="absolute right-0 mt-2 py-2 w-48 bg-white rounded-md shadow-xl z-20">
                {!isLoggedIn ? (
                  <button onClick={handleLogin} className="w-full text-left block px-4 py-2 text-sm capitalize text-gray-700 hover:bg-blue-500 hover:text-white">
                    Login
                  </button>
                ) : (
                  <>
                    <button onClick={handleLogout} className="w-full text-left block px-4 py-2 text-sm capitalize text-gray-700 hover:bg-blue-500 hover:text-white">
                      Logout
                    </button>
                  </>
                )}
              </div>
              )}
            </div>
          </div>
        </div>
      </header>
      <ProductList onAddToCart={handleAddToCart} onRemoveFromCart={handleRemoveFromCart} />
    </div>
  );
};

export default App
