import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faShoppingCart } from '@fortawesome/free-solid-svg-icons';

const Header = ({ cartItems, onUserClick, isLoggedIn }) => {
  return (
    <header className="bg-white py-4 flex justify-between items-center">
      <div className="container mx-auto px-4 flex justify-between items-center">
        <h1 className="text-3xl font-bold text-red-600">blocket</h1>
        <div className="flex items-center">
          <FontAwesomeIcon icon={faShoppingCart} className="text-xl mr-6 cursor-pointer" />
          {cartItems.length > 0 && (
            <span className="badge">{cartItems.length}</span> // Style this badge as needed
          )}
          <FontAwesomeIcon icon={faUser} className="text-xl ml-4 cursor-pointer" onClick={onUserClick} />
          {isLoggedIn ? 'Logout' : 'Login'}
        </div>
      </div>
    </header>
  );
};

export default Header;
