import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { redIcon, defaultIcon } from '../utils/MapIcons';

const MapComponent = ({ items, activeItem }) => {
  const safeItems = items || [];
  const defaultPosition = [57.7089, 11.9746]; // Coordinates for Gothenburg
  const defaultZoom = 13; // Zoom level for Gothenburg

  // Function to handle mouse enter on the marker
  const handleMouseEnter = (e, item) => {
    // Set the active item to the one being hovered
    setActiveItem(item);
  };

  // Function to handle mouse leave on the marker
  const handleMouseLeave = (e) => {
    // Reset the active item when not hovering over any marker
    setActiveItem(null);
  };

  return (
    <div className="h-96 w-full lg:w-4/5 xl:w-3/4 p-4 mx-auto my-4 rounded-lg shadow-lg">
      <MapContainer center={defaultPosition} zoom={defaultZoom} scrollWheelZoom={false} className="h-full w-full">
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {safeItems.map(item => (
          <Marker 
            key={item.productId} // Use the correct identifier
            position={[item.latitude, item.longitude]}
            icon={activeItem && activeItem.productId === item.productId ? redIcon : defaultIcon} // Use the correct identifier
            eventHandlers={{
              mouseover: (e) => handleMouseEnter(e, item),
              mouseout: (e) => handleMouseLeave(e)
            }}
          >
            <Popup>{item.description}</Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default MapComponent;
