import React, { useState } from 'react';
import ProductCard from './ProductCard';
import products from '../Products'; // Adjust the import path as necessary

const ProductList = ({ onAddToCart, onRemoveFromCart }) => {
  const [cartItems, setCartItems] = useState([]);

  const handleAddOrRemoveFromCart = (product) => {
    const isProductInCart = cartItems.some(item => item.id === product.id);

    if (isProductInCart) {
      // Remove from cart
      setCartItems(currentCartItems => currentCartItems.filter(item => item.id !== product.id));
      onRemoveFromCart(product.id); // Assuming you have a handler for this in App
    } else {
      // Add to cart
      setCartItems(currentCartItems => [...currentCartItems, product]);
      onAddToCart(product);
    }
  };

  return (
    <div className="container mx-auto px-4 py-6">
      <div className="grid grid-cols-1 gap-6">
        {products.map((product) => (
          <ProductCard
            key={product.id}
            {...product}
            onAddToCart={() => handleAddOrRemoveFromCart(product)}
            isInCart={cartItems.some((item) => item.id === product.id)}
          />
        ))}
      </div>
    </div>
  );
};

export default ProductList;
