// CurrencyContext.js
import React, { createContext, useState, useEffect } from 'react';

export const CurrencyContext = createContext();

export const CurrencyProvider = ({ children }) => {
  const [currency, setCurrency] = useState('SEK'); // Default currency
  const [conversionRates, setConversionRates] = useState({});

  // Fetch conversion rates when the component mounts
  useEffect(() => {
    const fetchConversionRates = async () => {
      // Use your chosen API endpoint here
      const response = await fetch('https://api.exchangerate-api.com/v4/latest/SEK');
      const data = await response.json();
      setConversionRates(data.rates);
    };

    fetchConversionRates();
  }, []);

  return (
    <CurrencyContext.Provider value={{ currency, setCurrency, conversionRates }}>
      {children}
    </CurrencyContext.Provider>
  );
};
