import React, { useState } from 'react';
import ProductList from './ProductList';
import MapComponent from './MapComponent';

const MapWithList = ({ products, cartItems, onAddToCart, onRemoveFromCart }) => {
  const [activeProduct, setActiveProduct] = useState(null);

  const handleMouseEnter = (product) => setActiveProduct(product);
  const handleMouseLeave = () => setActiveProduct(null);

  return (
    <div className="flex flex-row w-full">
      <div className="w-1/2"> {/* Adjust the width as necessary */}
        <ProductList
          products={products}
          cartItems={cartItems}
          onAddToCart={onAddToCart}
          onRemoveFromCart={onRemoveFromCart}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        />
      </div>
      <div className="w-1/2"> {/* Adjust the width as necessary */}
        <MapComponent items={products} activeItem={activeProduct} />
      </div>
    </div>
  );
};

export default MapWithList;
