import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const MapComponent = ({ locations, activeLocationId }) => {
  const mapRef = useRef(null);
  
  useEffect(() => {
    mapRef.current = L.map('map').setView([51.505, -0.09], 13);
    
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      maxZoom: 19,
      attribution: '© OpenStreetMap contributors',
    }).addTo(mapRef.current);
    
    // Add markers to the map
    locations.forEach(location => {
      const marker = L.marker([location.lat, location.lng]).addTo(mapRef.current);
      marker._leaflet_id = location.id;
    });
  }, []);

  useEffect(() => {
    if (activeLocationId) {
      const marker = mapRef.current._layers[activeLocationId];
      if (marker) {
        marker.setZIndexOffset(1000); // To highlight the marker
      }
    }
  }, [activeLocationId]);

  return <div id="map" style={{ height: '500px', width: '100%' }} />;
};

export default MapComponent;
