import React, { useState } from 'react';

const AdminPage = () => {
  const [products, setProducts] = useState([]); // List of products
  const [form, setForm] = useState({ // Form state
    Name: '',
    Description: '',
    Price: '',
    Category: '',
    ImageUrl: '',
    StockQuantity: '',
    Location: ''
  });

  // Handle form input changes
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setForm({ ...form, [name]: value });
  };

  // Handle form submission for adding/editing products
  const handleSubmit = (event) => {
    event.preventDefault();
    const newProduct = {
      ...form,
      ProductId: products.length + 1, // Normally you'd have a more robust ID system
    };
    setProducts([...products, newProduct]);
    setForm({ // Clear the form
      Name: '',
      Description: '',
      Price: '',
      Category: '',
      ImageUrl: '',
      StockQuantity: '',
      Location: ''
    });
  };

  // Delete a product by ProductId
  const handleDelete = (productId) => {
    setProducts(products.filter(product => product.ProductId !== productId));
  };

  return (
    <div className="container mx-auto p-6">
      <h2 className="text-4xl font-bold text-gray-800 mb-6">Admin - Product Management</h2>
      
      {/* Form for adding a product */}
      <div className="mb-8">
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Form inputs structured here */}
          {/* Repeat for each input field, following the pattern */}
          <div>
            <label className="block text-sm font-medium text-gray-700">Product Name</label>
            <input type="text" name="Name" value={form.Name} onChange={handleInputChange} className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50" required />
          </div>
          
          {/* Include input fields for Description, Price, Category, ImageUrl, StockQuantity, and Location following the pattern above */}

          {/* Submit Button */}
          <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-700">
            Add Product
          </button>
        </form>
      </div>

      {/* List of products with Delete button */}
      <div>
        {products.map((product) => (
          <div key={product.ProductId} className="flex justify-between items-center bg-white p-4 rounded-lg mb-4 shadow">
            <div>
              <h3 className="text-xl font-semibold">{product.Name}</h3>
              {/* Display other product details here */}
            </div>
            <div>
              <button onClick={() => handleDelete(product.ProductId)} className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-700">
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminPage;
