// src/context/CurrencyContext.js
import React, { createContext, useState, useEffect } from 'react';

export const CurrencyContext = createContext();

export const CurrencyProvider = ({ children }) => {
  const [currency, setCurrency] = useState('SEK');
  const [exchangeRates, setExchangeRates] = useState({});

  const fetchExchangeRates = async (newCurrency) => {
    try {
      const response = await fetch(`https://v6.exchangerate-api.com/v6/ea6fd159c1fb638bdb0bd796/latest/${newCurrency}`);
      const data = await response.json();
      setExchangeRates(data.conversion_rates);
    } catch (error) {
      console.error('Error fetching exchange rates:', error);
      setExchangeRates({});
    }
  };

  useEffect(() => {
    if (currency !== 'SEK') {
      fetchExchangeRates(currency);
    }
  }, [currency]);

  return (
    <CurrencyContext.Provider value={{ currency, setCurrency, exchangeRates }}>
      {children}
    </CurrencyContext.Provider>
  );
};
