// src/components/MapComponent.js
import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import { redIcon, defaultIcon } from '../utils/MapIcons';

// Add props for items and onHoverItem
const MapComponent = ({ items, activeItem, position, zoom }) => {
  return (
    <div className="h-96 w-full lg:w-4/5 xl:w-3/4 p-4 mx-auto my-4 rounded-lg shadow-lg">
      <MapContainer center={position} zoom={zoom} scrollWheelZoom={false} className="h-full w-full">
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {items.map(item => (
           <Marker 
              key={item.id}
              position={[item.latitude, item.longitude]}
              icon={activeItem && activeItem.id === item.id ? redIcon : defaultIcon}
            >
            <Popup>{item.description}</Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};


export default MapComponent;
