import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import ProductList from './components/ProductList';
import { useNavigate } from 'react-router-dom';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [cartItems, setCartItems] = useState([]);
  const navigate = useNavigate();

  // Function to add or remove items from the cart
  const handleCart = (product, action) => {
    setCartItems((prevItems) => {
      if (action === 'add') {
        return [...prevItems, product];
      } else {
        return prevItems.filter((item) => item.id !== product.id);
      }
    });
  };

  // Navigate to login page
  const handleLogin = () => navigate('/login');

  // Logout the user
  const handleLogout = () => setIsLoggedIn(false);

  // Handle clicks outside of user dropdown to close it
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (event.target.id !== 'user-dropdown') {
        setIsLoggedIn(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div className="App">
      <div className="bg-gradient-to-r from-red-500 to-yellow-500 to-green-500 to-blue-500 to-indigo-500 to-purple-500 h-1" />
      <header className="bg-white py-4 flex justify-between items-center shadow">
        <h1 className="text-3xl font-bold text-red-600 ml-4">blocket</h1>
        <div className="flex items-center mr-4">
          <div className="relative">
            <FontAwesomeIcon
              icon={faShoppingCart}
              className="text-xl mr-4 cursor-pointer"
              onClick={() => navigate('/cart')} // Assuming '/cart' is the cart page route
            />
            {cartItems.length > 0 && (
              <span className="absolute top-0 right-0 inline-flex items-center justify-center px-2 py-1 text-xs font-bold leading-none text-red-100 transform translate-x-1/2 -translate-y-1/2 bg-red-600 rounded-full">
                {cartItems.length}
              </span>
            )}
          </div>
          <FontAwesomeIcon
            icon={faUser}
            className="text-xl cursor-pointer"
            onClick={() => setIsLoggedIn(!isLoggedIn)}
            id="user-dropdown"
          />
          {isLoggedIn && (
            <div className="absolute right-0 mt-2 py-2 w-48 bg-white rounded-md shadow-xl z-20">
              <button onClick={handleLogout} className="w-full text-left block px-4 py-2 text-sm capitalize text-gray-700 hover:bg-blue-500 hover:text-white">
                Logout
              </button>
            </div>
          )}
        </div>
      </header>
      <ProductList onToggleCartItem={handleCart} />
    </div>
  );
};

export default App;
