import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartPlus } from '@fortawesome/free-solid-svg-icons';

const ProductCard = ({ product, onAddToCart }) => {
  return (
    <div className="bg-white p-4 flex items-center shadow rounded-md">
      <img src={product.imageUrl} alt={product.title} className="w-24 h-24 mr-4" />
      <div className="flex-grow">
        <div className="text-sm text-gray-600">{product.category}</div>
        <div className="text-lg font-medium">{product.title}</div>
        <div className="text-xl font-bold">{product.price}</div>
        <div className="text-sm text-gray-500">{product.time}</div>
      </div>
      <button 
        className="flex flex-col justify-between items-center ml-4"
        onClick={() => onAddToCart(product)}
      >
        <FontAwesomeIcon icon={faCartPlus} className="text-gray-400 text-xl cursor-pointer" />
      </button>
    </div>
  );
};

export default ProductCard;
