import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import MapWithList from './components/MapWithList';
import 'react-toastify/dist/ReactToastify.css';

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const [products, setProducts] = useState([]);


  // Load cart items from localStorage when the component mounts
  useEffect(() => {
    const storedCartItems = localStorage.getItem('cartItems');
    if (storedCartItems) {
      setCartItems(JSON.parse(storedCartItems));
    }
  }, []);

  // Save cart items to localStorage when they change
  useEffect(() => {
    localStorage.setItem('cartItems', JSON.stringify(cartItems));
  }, [cartItems]);

  const handleAddToCart = (product) => {
    setCartItems(prevItems => {
      const existingItem = prevItems.find(item => item.productId === product.id);
      if (existingItem) {
        // Increase quantity if already in cart
        return prevItems.map(item =>
          item.productId === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        // Add new item to cart
        return [...prevItems, { ...product, productId: product.id, quantity: 1 }];
      }
    });
  };

  const handleRemoveFromCart = (productId) => {
    setCartItems(prevItems => {
      const existingItem = prevItems.find(item => item.productId === productId);
      if (existingItem && existingItem.quantity > 1) {
        // Decrease quantity
        return prevItems.map(item =>
          item.productId === productId ? { ...item, quantity: item.quantity - 1 } : item
        );
      } else {
        // Remove item from cart
        return prevItems.filter(item => item.productId !== productId);
      }
    });
  };



  return (
    <div className="App">
      <div className="bg-gradient-to-r from-red-500 to-yellow-500 to-green-500 to-blue-500 to-indigo-500 to-purple-500 h-1"></div>
      <Header
        cartItems={cartItems}
        onRemoveFromCart={handleRemoveFromCart}
        onAddToCart={handleAddToCart}  // Pass onAddToCart as a prop
      />
      <main className="flex flex-col md:flex-row">
        <MapWithList
          products={products}
          cartItems={cartItems}
          onAddToCart={handleAddToCart}
          onRemoveFromCart={handleRemoveFromCart}
        />
      </main>
    </div>
  );
};

export default App;
