import React, { useState, useContext } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { CurrencyContext } from '../contexts/CurrencyContext';
import { useAuth } from '../contexts/AuthContext'; // Import the useAuth hook
import { ToastContainer, toast } from 'react-toastify';

const CheckoutPage = () => {
  const { currency, exchangeRates } = useContext(CurrencyContext);
  const { authData } = useAuth(); // Use the useAuth hook to get auth data
  var [cartItems, setCartItems] = useState([]);
  // Declare email as a state variable instead of a constant variable
  const [email, setEmail] = useState(''); // Initialize with an empty string

  // If the user is logged in, get their email from the authData token
  const loggedInEmail = authData ? getEmailFromToken(authData.token) : null;

  const location = useLocation();
  const navigate = useNavigate();
  cartItems = location.state?.cartItems ?? [];

  const handleEmailChange = (e) => {
    setEmail(e.target.value); // Correctly update the state
  };

  const convertPrice = (price) => {
    const numericPrice = parseFloat(price);
    if (isNaN(numericPrice)) {
      console.error('Price is not a valid number:', price);
      return 'Invalid price';
    }

    const rate = exchangeRates[currency];
    if (!rate) {
      // Log the error and show the default SEK price
      console.error('Exchange rate not found for currency:', currency);
      return `${numericPrice} SEK`; // Fallback to SEK
    }

    return (numericPrice * rate).toFixed(2);
  };

  const calculateTotal = (items) => {
    return items.reduce((total, item) => {
      const price = convertPrice(item.price, currency, exchangeRates); // Ensure you pass the currency and exchangeRates
      return total + parseFloat(price);
    }, 0).toFixed(2) + ` ${currency}`;
  };

  const handleCheckout = async () => {
    // Construct order data
    const orderData = {
      email: authData ? loggedInEmail : email, // Use logged-in email or the one provided in the form
      totalPrice: calculateTotal(cartItems),
      items: cartItems.map(item => ({
        productId: item.productId,
        quantity: item.quantityInCart
      }))
    };
  
    // Call the API to create an order
    try {
      const response = await fetch('/api/Orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // Include the auth token in the request if the user is logged in
          ...(authData ? { 'Authorization': `Bearer ${authData.token}` } : {})
        },
        body: JSON.stringify(orderData)
      });
  
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
  
      // Handle success
      toast.success('Checkout successful! Redirecting...', {
        position: "top-center",
        autoClose: 2000,
        onClose: () => {
          navigate('/'); // Redirect to the home page
          setCartItems([]); // Clear the cart state
          localStorage.setItem('cartItems', JSON.stringify([])); // Clear the cart in localStorage
        }
      });
    } catch (error) {
      // Handle errors
      toast.error('Checkout failed. Please try again.', {
        position: "top-center",
        autoClose: 2000,
      });
      console.error('Checkout failed:', error);
    }
  };
  

  function getEmailFromToken(token) {
    try {
      const base64Url = token.split('.')[1];
      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
      }).join(''));
      
      //console.log('Decoded token:', jsonPayload);
      return JSON.parse(jsonPayload).sub; // make sure your token has an email claim
    } catch (e) {
      console.error('Error decoding token:', e);
      return null;
    }
  }

  const navigateHome = () => {
    navigate('/'); // Navigate to the home page
  };

    return (
    <>
      <div className="bg-gradient-to-r from-red-500 to-yellow-500 to-green-500 to-blue-500 to-indigo-500 to-purple-500 h-1"></div>
      <header className="bg-white py-4 flex justify-between items-center">
        <div className="container mx-auto px-4 flex justify-between items-center">
          <h1 className="text-3xl font-bold text-red-600 cursor-pointer" onClick={navigateHome}>blocket</h1>
        </div>
      </header>
      <div className="container mx-auto p-4 grid grid-cols-2 gap-14">
        <div className="col-span-1">
          <h2 className="text-2xl font-bold mb-4">Your Cart</h2>
          {cartItems.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <ul className="mb-4">
              {cartItems.map((item) => (
                <li key={item.id} className="flex justify-between items-center mb-2">
                  <div className="flex items-center">
                    <img src={item.imageUrl} alt={item.title} className="w-16 h-16 mr-4 rounded-md" />
                    <span>{item.name}</span>
                  </div>
                  <span>{convertPrice(item.price) + " "}{currency}</span>
                </li>
              ))}
            </ul>
          )}
        </div>
        <div className="flex-1">
          <h2 className="text-2xl font-bold mb-4">Payment Details</h2>
          {!authData && (
            <input
              type="email"
              value={email}
              onChange={handleEmailChange}
              placeholder="Enter your email"
              className="border p-2 w-full mb-4"
              required
            />
          )}
          {authData && <p>Billing to: {loggedInEmail}</p>}
          <div className="flex justify-between items-center font-bold mb-4">
            <span>Total:</span>
            <span>{calculateTotal(cartItems)}</span>
          </div>
          <button
            onClick={handleCheckout}
            className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-700 transition-colors"
            disabled={cartItems.length === 0}
          >
            Confirm and Pay
          </button>
        </div>
      </div>
      <ToastContainer position="top-center" autoClose={2000} hideProgressBar newestOnTop closeOnClick rtl={false} pauseOnFocusLoss draggable pauseOnHover style={{ zIndex: 9999 }} />
    </>
  );
};

export default CheckoutPage;
