import React, {useContext} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartShopping, faCheckCircle } from '@fortawesome/free-solid-svg-icons';
import { CurrencyContext } from '../context/CurrencyContext';

const ProductCard = ({ product, isInCart, onToggleInCart }) => {

  const { currency, exchangeRates } = useContext(CurrencyContext);

  const convertedPrice = (price) => {
    const rate = exchangeRates[currency] || 1; // Default to 1 if rate is not fetched yet
    return (price * rate).toFixed(2);
  };

  return (
    <div className="bg-white p-4 flex items-center shadow rounded-md">
      <img src={product.imageUrl} alt={product.title} className="w-24 h-24 mr-4" />
      <div className="flex-grow">
        <div className="text-sm text-gray-600">{product.category}</div>
        <div className="text-lg font-medium">{product.title}</div>
      </div>
      <div className="text-right">
        <div className="text-xl font-bold">{convertPrice(product.price)}{currency}</div>
        <div className="text-sm text-gray-500">{product.time}</div>
      </div>
      <div className="flex flex-col justify-between items-center ml-4">
        {product.shipping && (
          <span className="bg-blue-100 text-blue-800 text-xs font-semibold px-2.5 py-0.5 rounded-full mb-2">
            Free Shipping
          </span>
        )}
        <FontAwesomeIcon
          icon={isInCart ? faCheckCircle : faCartShopping}
          className={`text-xl cursor-pointer ${isInCart ? 'text-green-500' : 'text-gray-400'}`}
          onClick={onToggleInCart}
        />
      </div>
    </div>
  );
};

export default ProductCard;
