import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faShoppingCart } from '@fortawesome/free-solid-svg-icons';

const AdminPage = () => {
  const initialFormState = {
    Name: '',
    Description: '',
    Price: '',
    Category: '',
    ImageUrl: '',
    StockQuantity: '',
    Location: ''
  };

  const [products, setProducts] = useState([]);
  const [currentProduct, setCurrentProduct] = useState(initialFormState);
  const [isAdding, setIsAdding] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentProduct({ ...currentProduct, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (currentProduct.ProductId) {
      // Editing existing product
      setProducts(products.map((product) => (product.ProductId === currentProduct.ProductId ? currentProduct : product)));
    } else {
      // Adding new product
      setProducts([...products, { ...currentProduct, ProductId: products.length + 1 }]);
    }
    setCurrentProduct(initialFormState);
    setIsAdding(false);
  };

  const handleDelete = (productId) => {
    setProducts(products.filter((product) => product.ProductId !== productId));
  };

  const handleEditClick = (product) => {
    setCurrentProduct(product);
    setIsAdding(true);
  };

  const handleAddNewClick = () => {
    setCurrentProduct(initialFormState);
    setIsAdding(true);
  };

  const closeModal = () => {
    setIsAdding(false);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <div className="bg-gradient-to-r from-red-500 to-yellow-500 to-green-500 to-blue-500 to-indigo-500 to-purple-500 h-1"></div>
      <header className="bg-white py-4 flex justify-between items-center">
        <div className="container mx-auto px-4 flex justify-between items-center">
          <h1 className="text-3xl font-bold text-red-600">blocket AdminPanel</h1>
          <div className="flex items-center">
            <FontAwesomeIcon icon={faShoppingCart} className="text-xl mr-6 cursor-pointer" />
            <div className="relative" ref={dropdownRef}>
              <FontAwesomeIcon icon={faUser} className="text-xl cursor-pointer" onClick={handleLoginLogoutClick} />
              {showDropdown && (
                <div className="absolute right-0 mt-2 py-2 w-48 bg-white rounded-md shadow-xl z-20">
                    <>
                      <button onClick={handleLogout} className="w-full text-left block px-4 py-2 text-sm capitalize text-gray-700 hover:bg-blue-500 hover:text-white">
                        Logout
                      </button>
                    </>
                </div>
              )}
            </div>
          </div>
        </div>
      </header>
      
      <button 
        onClick={handleAddNewClick} 
        className="mb-4 bg-green-500 text-white p-2 rounded-full shadow-lg text-xl hover:bg-green-600"
      >
        +
      </button>

      {products.map((product) => (
        <div key={product.ProductId} className="flex items-center justify-between p-4 mb-2 bg-white shadow rounded">
          <img src={product.ImageUrl} alt={product.Name} className="w-16 h-16 mr-4" />
          <div>
            <h3 className="font-bold">{product.Name}</h3>
            <p>{product.Description}</p>
            <p>{product.Price}</p>
            <p>{product.Category}</p>
          </div>
          <div>
            <button onClick={() => handleEditClick(product)} className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded mr-2">
              Edit
            </button>
            <button onClick={() => handleDelete(product.ProductId)} className="bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded">
              Delete
            </button>
          </div>
        </div>
      ))}

      {/* Modal for adding/editing a product */}
      {isAdding && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full" id="my-modal">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3 text-center">
              <h3 className="text-lg leading-6 font-medium text-gray-900">Product Details</h3>
              <div className="mt-2 px-7 py-3">
                <form onSubmit={handleSubmit}>
                  <input type="text" name="Name" placeholder="Product Name" value={currentProduct.Name} onChange={handleInputChange} className="mt-2 px-3 py-2 border rounded-md w-full" />
                  <textarea name="Description" placeholder="Description" value={currentProduct.Description} onChange={handleInputChange} className="mt-2 px-3 py-2 border rounded-md w-full"></textarea>
                  <input type="text" name="Price" placeholder="Price" value={currentProduct.Price} onChange={handleInputChange} className="mt-2 px-3 py-2 border rounded-md w-full" />
                  <input type="text" name="Category" placeholder="Category" value={currentProduct.Category} onChange={handleInputChange} className="mt-2 px-3 py-2 border rounded-md w-full" />
                  <input type="text" name="ImageUrl" placeholder="Image URL" value={currentProduct.ImageUrl} onChange={handleInputChange} className="mt-2 px-3 py-2 border rounded-md w-full" />
                  <input type="number" name="StockQuantity" placeholder="Stock Quantity" value={currentProduct.StockQuantity} onChange={handleInputChange} className="mt-2 px-3 py-2 border rounded-md w-full" />
                  <input type="text" name="Location" placeholder="Location" value={currentProduct.Location} onChange={handleInputChange} className="mt-2 px-3 py-2 border rounded-md w-full" />
                  <div className="items-center px-4 py-3">
                    <button id="ok-btn" className="px-4 py-2 bg-blue-500 text-white text-base font-medium rounded-md w-full shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-300">
                      {currentProduct.ProductId ? 'Update' : 'Add'}
                    </button>
                  </div>
                </form>
              </div>
              <div className="items-center px-4 py-3">
                <button onClick={closeModal} className="px-4 py-2 bg-gray-500 text-white text-base font-medium rounded-md w-full shadow-sm hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-300">
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPage;
