import React, { useState, useEffect } from 'react';
import ProductCard from './ProductCard';
import apiEndpoints from '../configs/ApiEndpoints';

const ProductList = ({ cartItems, setCartItems, onMouseEnter, onMouseLeave }) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch(apiEndpoints.products);
      if (response.ok) {
        const data = await response.json();
        setProducts(data);
      }
    };
    fetchProducts();
  }, []);

  //console.log("Product IDs:", products.map(p => p.productId)); // Updated from p.id to p.productId

  const handleToggleInCart = (product) => {
    setCartItems(currentItems => {
      // Check if the item is already in the cart
      const isInCart = currentItems.some(item => item.productId === product.productId);
      if (isInCart) {
        // Remove the item from the cart
        return currentItems.filter(item => item.productId !== product.productId);
      } else {
        // Add the item to the cart with a quantity of 1 (or increment quantity)
        return [...currentItems, { ...product, quantity: 1 }];
      }
    });
  };
  

  if (products.length === 0) {
    return (
      <div className="container mx-auto px-4 py-6 text-center">
        <p>Oh no! It looks like all our products have gone on vacation! üèñÔ∏è</p>
        <p>Check back soon, or our products might send you a postcard!</p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 gap-6 p-10">
      {products
      .filter(product => product.stockQuantity > 0) // Filter out products with 0 quantity
      .map(product => (
        <ProductCard
          key={product.productId}
          product={product}
          isInCart={cartItems.some(item => item.productId === product.productId)}
          onToggleInCart={() => handleToggleInCart(product)}
          onMouseEnter={() => onMouseEnter(product)}
          onMouseLeave={() => onMouseLeave()}
        />
    ))}
    </div>
  );
};

export default ProductList;
