import React, { useState, useEffect } from 'react';
import ProductCard from './ProductCard';
import apiEndpoints from '../configs/ApiEndpoints';

const ProductList = ({ cartItems, onAddToCart, onRemoveFromCart }) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch(apiEndpoints.products);
      const data = await response.json();
      setProducts(data);
    };

    fetchProducts();
  }, []);

  const handleAddToCart = (productId) => {
    // Find the item in the cart
    const existingItem = cartItems.find(item => item.id === productId);
  
    // If the item exists, increase the quantity
    if (existingItem) {
      onAddToCart({ ...existingItem, quantity: existingItem.quantity + 1 });
    } else {
      // If the item doesn't exist, add it to the cart with quantity 1
      const product = products.find(item => item.id === productId);
      onAddToCart({ ...product, quantity: 1 });
    }
  };

  const handleRemoveOneFromCart = (productId) => {
    // Find the item in the cart
    const existingItem = cartItems.find(item => item.id === productId);
  
    // If the item exists and quantity is greater than 1, reduce the quantity
    if (existingItem && existingItem.quantity > 1) {
      onRemoveFromCart({ ...existingItem, quantity: existingItem.quantity - 1 });
    } else if (existingItem) {
      // If the item exists and quantity is 1, remove the item from the cart
      onRemoveFromCart({ ...existingItem, quantity: 0 });
    }
  };
  

  const onToggleInCart = (product) => {
    const isInCart = cartItems.some(item => item.id === product.id);
    if (isInCart) {
      onRemoveFromCart(product.id);
    } else {
      onAddToCart(product);
    }
  };

  return (
    <div className="container mx-auto px-4 py-6">
      <div className="grid grid-cols-1 gap-6">
        {products.map(product => {
          const isInCart = cartItems.some(item => item.id === product.id);
          
          return (
            <ProductCard
              key={product.id}
              product={product}
              isInCart={isInCart}
              onToggleInCart={() => onToggleInCart(product)}
              onAddToCart={() => handleAddToCart(product.id)}
              onRemoveFromCart={() => handleRemoveOneFromCart(product.id)}
            />
          );
        })}
      </div>
    </div>
  );
};

export default ProductList;
