import React from 'react';
import ProductCard from './ProductCard';
import products from '../components/Products'; // Adjust the import path as necessary

const ProductList = ({ cartItems, onAddToCart, onRemoveFromCart }) => {
  const handleToggleInCart = (product) => {
    const isInCart = cartItems.some(item => item.id === product.id);
    if (isInCart) {
      onRemoveFromCart(product.id);
    } else {
      onAddToCart(product);
    }
  };

  return (
    <div className="container mx-auto px-4 py-6">
      <div className="grid grid-cols-1 gap-6">
        {products.map(product => (
          <ProductCard
            key={product.id}
            product={product}
            isInCart={cartItems.some(item => item.id === product.id)}
            onToggleInCart={() => handleToggleInCart(product)}
          />
        ))}
      </div>
    </div>
  );
};

export default ProductList;
