// ProductCard.js
import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartPlus, faCheck } from '@fortawesome/free-solid-svg-icons';

const ProductCard = ({ product, onAddToCart, isInCart }) => {
  return (
    <div className="bg-white p-4 flex items-center shadow rounded-md">
      <img src={product.imageUrl} alt={product.alt} className="w-24 h-24 mr-4" />
      <div className="flex-grow">
        <div className="text-sm text-gray-600">{product.category}</div>
        <div className="text-lg font-medium">{product.title}</div>
        <div className="text-xl font-bold">{product.price}</div>
        <div className="text-sm text-gray-500">{product.time}</div>
      </div>
      <div className="text-right">
        {product.shipping && (
          <span className="bg-blue-100 text-blue-800 text-xs font-semibold px-2.5 py-0.5 rounded-full mb-2">
            Frakt med k√∂pskydd
          </span>
        )}
        <FontAwesomeIcon
          icon={isInCart ? faCheck : faCartPlus}
          className={`text-xl cursor-pointer ${isInCart ? 'text-green-500' : 'text-gray-400'}`}
          onClick={() => onAddToCart(product)}
        />
      </div>
    </div>
  );
};

export default ProductCard;
