import React, { useState, useRef, useEffect } from 'react';
import ProductCard from './ProductCard';
import products from '../Products'; // Adjust the import path as necessary



const ProductList = () => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    console.log('Cart Items:', cartItems);
  }, [cartItems]);  

  // Function to add item to cart
  const handleAddToCart = (product) => {
    setCartItems(prevCartItems => {
      const productExists = prevCartItems.find(item => item.id === product.id);
      if (productExists) {
        return prevCartItems.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevCartItems, { ...product, quantity: 1 }];
    });
  };

  // Function to remove item from cart
  const handleRemoveFromCart = (productId) => {
    setCartItems(prevCartItems =>
      prevCartItems.filter(item => item.id !== productId)
    );
  };



  return (
    <div className="container mx-auto px-4 py-6">
      <div className="grid grid-cols-1 gap-6">
      <ProductList onAddToCart={handleAddToCart} onRemoveFromCart={handleRemoveFromCart} />
      </div>
    </div>
  );
};

export default ProductList;
