import React, { useContext } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartShopping, faCheckCircle } from '@fortawesome/free-solid-svg-icons';
import { CurrencyContext } from '../contexts/CurrencyContext';
import { faPlus } from '@fortawesome/free-solid-svg-icons';

const ProductCard = ({ product, isInCart, onToggleInCart }) => {
  const { currency, exchangeRates } = useContext(CurrencyContext);

  const formattedPrice = new Intl.NumberFormat('en-US', { style: 'currency', currency }).format(product.price);

  return (
    <div className="flex justify-between items-center p-4 bg-white rounded-lg shadow">
      <div className="flex items-center space-x-4">
        <img src={product.imageUrl} alt={product.name} className="w-24 h-24 object-cover rounded-lg" />
        <div>
          <h3 className="text-lg font-semibold">{product.name}</h3>
          <p className="text-sm text-gray-500">{product.category}</p>
          <p className="text-gray-600 truncate w-90">{product.description}</p>
          <p className="mt-1 text-xs uppercase tracking-wide text-gray-400">{product.location}</p>
        </div>
      </div>
      <div className="text-right">
        <p className="text-xl font-bold">{formattedPrice}</p>
        <p className="text-gray-500">Qty: {product.stockQuantity}</p>
      </div>
      <button
        className={`ml-4 p-2 rounded-full ${isInCart ? 'bg-green-500' : 'bg-gray-300 hover:bg-gray-400'}`}
        onClick={onToggleInCart}
      >
        <FontAwesomeIcon
          icon={isInCart ? faCheckCircle : faCartShopping}
          className={`text-xl ${isInCart ? 'text-white' : 'text-gray-600'}`}
        />
      </button>
    </div>
  );
};

export default ProductCard;
