import React from 'react';
import { useNavigate } from 'react-router-dom';

const CheckoutPage = ({ cartItems }) => {
  const navigate = useNavigate();

  // Assuming each item has 'price' and 'quantity' properties
  const calculateTotal = (items) => {
    return items.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2);
  };

  const handleCheckout = () => {
    console.log('Handling checkout...');
    navigate('/success'); // Navigate to a success page after checkout
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Checkout</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <div className="space-y-4">
            {cartItems.map((item) => (
              <div key={item.id} className="flex justify-between items-center p-4 shadow rounded-md">
                <img src={item.imageUrl} alt={item.title} className="w-20 h-20 object-cover rounded mr-4" />
                <div className="flex-grow">
                  <div className="text-lg font-medium">{item.title}</div>
                  <div className="text-sm text-gray-600">{item.category}</div>
                </div>
                <div className="text-right">
                  <div className="text-xl font-bold">{item.quantity} x {item.price.toFixed(2)} kr</div>
                </div>
              </div>
            ))}
          </div>
          <div className="flex justify-between items-center font-bold my-4 p-4 shadow rounded-md">
            <span>Total:</span>
            <span>{calculateTotal(cartItems)} kr</span>
          </div>
          <button
            onClick={handleCheckout}
            className="w-full bg-blue-500 text-white p-2 rounded-md hover:bg-blue-700 transition-colors"
            disabled={cartItems.length === 0}
          >
            Confirm and Pay
          </button>
        </>
      )}
    </div>
  );
};

export default CheckoutPage;
