import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import Header from './Header'; // Import your header component if you have one

const CheckoutPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  
  // Safeguard against undefined cartItems
  const { cartItems = [] } = location.state ?? {};

  const calculateTotal = (items) => {
    return items.reduce((total, item) => total + (item.price * item.quantity), 0).toFixed(2);
  };

  const handleCheckout = () => {
    console.log('Handling checkout...');
    navigate('/success');
  };

  return (
    <div>
      <Header /> {/* Include your header component */}
      <div className="container mx-auto p-4">
        <h2 className="text-2xl font-bold mb-4">Checkout</h2>
        {cartItems.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <>
            <ul className="mb-4">
              {cartItems.map((item) => (
                <li key={item.id} className="flex justify-between items-center mb-2 p-4 border-b">
                  <div className="flex items-center">
                    <img src={item.imageUrl} alt={item.title} className="w-16 h-16 object-cover rounded mr-4" />
                    <div>
                      <div className="text-lg font-medium">{item.title}</div>
                      <div className="text-sm text-gray-500">{item.quantity} x {item.price} kr</div>
                    </div>
                  </div>
                  <span className="text-lg font-bold">{(item.price * item.quantity).toFixed(2)} kr</span>
                </li>
              ))}
            </ul>
            <div className="flex justify-between items-center font-bold mb-4 p-4 border-t">
              <span>Total:</span>
              <span>{calculateTotal(cartItems)} kr</span>
            </div>
            <button
              onClick={handleCheckout}
              className="w-full bg-blue-600 text-white p-3 rounded-md hover:bg-blue-700 transition-colors"
              disabled={cartItems.length === 0}
            >
              Confirm and Pay
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default CheckoutPage;
