import React, { useContext } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartShopping, faCheckCircle } from '@fortawesome/free-solid-svg-icons';
import { CurrencyContext } from '../contexts/CurrencyContext';
import { faPlus, faMinus } from '@fortawesome/free-solid-svg-icons';

const ProductCard = ({ product, isInCart, onAddToCart, onRemoveFromCart }) => {
  const { currency, exchangeRates } = useContext(CurrencyContext);

  const formattedPrice = new Intl.NumberFormat('en-US', { style: 'currency', currency }).format(product.price);

  const handleIncreaseQuantity = () => {
    if (product.stockQuantity > product.quantityInCart) {
      onAddToCart(product.id);
    } else {
      alert("Cannot add more. Not enough stock.");
    }
  };

  const handleDecreaseQuantity = () => {
    if (product.quantityInCart > 1) {
      onRemoveFromCart(product.id);
    }
  };

  return (
    <div className="flex justify-between items-center p-4 bg-white rounded-md shadow">
      <div className="flex items-center">
        <img src={product.imageUrl} alt={product.title} className="w-24 h-24 object-cover rounded-lg mr-4" />
        <div>
          <h3 className="text-lg font-semibold">{product.title}</h3>
          <p className="text-gray-600">{product.description}</p>
          <p className="text-gray-500">{product.category} - {product.location}</p>
        </div>
      </div>
      <div className="text-right">
        <p className="text-xl font-bold">{formattedPrice}</p>
        <p className="text-gray-500">Qty: {product.stockQuantity}</p>
      </div>
      <div className="flex items-center">
        <button
          className={`p-2 rounded-lg ${isInCart ? 'bg-green-500' : 'bg-gray-200'}`}
          onClick={handleIncreaseQuantity}
        >
          <FontAwesomeIcon icon={faPlus} className="text-white" />
        </button>
        <button
          className={`ml-4 p-2 rounded-lg ${isInCart ? 'bg-green-500' : 'bg-gray-200'}`}
          onClick={onToggleInCart}
        >
          <FontAwesomeIcon
            icon={isInCart ? faCheckCircle : faCartShopping}
            className={`text-xl ${isInCart ? 'text-white' : 'text-gray-400'}`}
          />
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
