import React, { useState, useEffect, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import ProductList from './components/ProductList';
import { useNavigate } from 'react-router-dom';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [cartVisible, setCartVisible] = useState(false);
  const [cartItems, setCartItems] = useState([]);
  const navigate = useNavigate();
  const cartRef = useRef();

  useEffect(() => {
    // Clicking outside the cart dropdown will close it
    function handleClickOutside(event) {
      if (cartRef.current && !cartRef.current.contains(event.target)) {
        setCartVisible(false);
      }
    }

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleCartIconClick = () => {
    setCartVisible(!cartVisible);
  };

  const handleCheckout = () => {
    // Handle checkout logic here
    navigate('/checkout'); // Assuming '/checkout' is your checkout page route
  };

  // Add product to cart
  const handleAddToCart = (product) => {
    setCartItems((prevItems) => [...prevItems, product]);
  };

  // Remove product from cart
  const handleRemoveFromCart = (productId) => {
    setCartItems((prevItems) => prevItems.filter((item) => item.id !== productId));
  };

  return (
    <div className="App">
      {/* ... header and other components ... */}
      <div className="flex justify-end items-center relative mr-4">
        <FontAwesomeIcon
          icon={faShoppingCart}
          className={`text-xl mr-6 cursor-pointer ${cartItems.length > 0 ? 'text-green-600' : 'text-gray-400'}`}
          onClick={handleCartIconClick}
        />
        {cartItems.length > 0 && (
          <span className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full h-6 w-6 flex items-center justify-center text-xs">
            {cartItems.length}
          </span>
        )}
        {cartVisible && (
          <div className="absolute right-0 mt-12 w-64 bg-white rounded-md shadow-xl z-20 p-4" ref={cartRef}>
            {cartItems.length === 0 ? (
              <div className="text-center text-gray-500">Your cart is empty.</div>
            ) : (
              <ul>
                {cartItems.map((item) => (
                  <li key={item.id} className="flex justify-between items-center mb-2">
                    <span>{item.name}</span>
                    <button
                      onClick={() => handleRemoveFromCart(item.id)}
                      className="bg-red-500 text-white px-2 py-1 rounded text-xs"
                    >
                      Remove
                    </button>
                  </li>
                ))}
              </ul>
            )}
            <button
              onClick={handleCheckout}
              className="w-full bg-blue-500 text-white p-2 rounded mt-4 hover:bg-blue-600"
              disabled={cartItems.length === 0}
            >
              Checkout
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
