// src/App.js
import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import ProductList from './components/ProductList';
import MapComponent from './components/MapComponent';
import MapWithList from './components/MapWithList';
import 'react-toastify/dist/ReactToastify.css';

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const defaultPosition = [59.3293, 18.0686]; // Coordinates for Stockholm, Sweden
  const defaultZoom = 8;

  // Load cart items from localStorage when the component mounts
  useEffect(() => {
    const storedCartItems = localStorage.getItem('cartItems');
    if (storedCartItems) {
      setCartItems(JSON.parse(storedCartItems));
    }
  }, []);

  // Save cart items to localStorage when they change
  useEffect(() => {
    localStorage.setItem('cartItems', JSON.stringify(cartItems));
  }, [cartItems]);

  const handleAddToCart = (product) => {
    setCartItems((prevItems) => {
        const existingProductIndex = prevItems.findIndex(item => item.id === product.id);

        if (existingProductIndex >= 0) {
            const updatedCartItems = [...prevItems];
            updatedCartItems[existingProductIndex] = {
                ...updatedCartItems[existingProductIndex],
                quantityInCart: updatedCartItems[existingProductIndex].quantityInCart + 1
            };
            localStorage.setItem('cartItems', JSON.stringify(updatedCartItems));
            return updatedCartItems;
        } else {
            const newCartItems = [...prevItems, { ...product, quantityInCart: 1 }];
            localStorage.setItem('cartItems', JSON.stringify(newCartItems));
            return newCartItems;
        }
    });
};


  const handleRemoveFromCart = (productId) => {
    setCartItems((prevItems) => {
      const newCartItems = prevItems.filter((item) => item.id !== productId);
      localStorage.setItem('cartItems', JSON.stringify(newCartItems));
      return newCartItems;
    });
  };

  return (
    <div className="App">
      <div className="bg-gradient-to-r from-red-500 to-yellow-500 to-green-500 to-blue-500 to-indigo-500 to-purple-500 h-1"></div>
      <Header cartItems={cartItems} onRemoveFromCart={handleRemoveFromCart} />
      <main className="flex flex-col md:flex-row">
        <ProductList 
          cartItems={cartItems}
          onAddToCart={handleAddToCart} 
          onRemoveFromCart={handleRemoveFromCart}
        />
        <MapWithList />
      </main>
    </div>
  );
};

export default App;
