import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../context/CartContext'; // Adjust the import path as necessary

const CheckoutPage = () => {
    const { cartItems, clearCart } = useContext(CartContext); // Use clearCart if you have a function to clear the cart after checkout
    const navigate = useNavigate();

    const calculateTotal = (items) => {
        return items.reduce((total, item) => total + item.price * item.quantity, 0);
    };

    const handleCheckout = () => {
        // Here you would usually handle the payment process
        console.log('Handling checkout...');
        // After checkout, you might want to navigate to a success page or clear the cart
        clearCart(); // Assuming you have a function to clear the cart
        navigate('/success'); // Navigate to the success page
    };

    return (
        <div className="container mx-auto p-4">
            <h2 className="text-2xl font-bold mb-4">Checkout</h2>
            {cartItems.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <>
                    <ul className="mb-4">
                        {cartItems.map((item) => (
                            <li key={item.id} className="flex justify-between items-center mb-2">
                                <span>{item.title}</span>
                                <span>{item.quantity} x {item.price.toFixed(2)} kr</span>
                            </li>
                        ))}
                    </ul>
                    <div className="flex justify-between items-center font-bold mb-4">
                        <span>Total:</span>
                        <span>{calculateTotal(cartItems).toFixed(2)} kr</span>
                    </div>
                    <button
                        onClick={handleCheckout}
                        className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-700 transition-colors"
                        disabled={cartItems.length === 0}
                    >
                        Confirm and Pay
                    </button>
                </>
            )}
        </div>
    );
};

export default CheckoutPage;
