import React, { useState, useRef, useEffect } from 'react';
import ProductCard from './ProductCard';
import products from '../Products'; // Adjust the import path as necessary



const ProductList = () => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    console.log('Cart Items:', cartItems);
  }, [cartItems]);  

  const handleAddOrRemoveFromCart = (product) => {
    setCartItems((currentCartItems) => {
      const isProductInCart = currentCartItems.some((item) => item.id === product.id);
      if (isProductInCart) {
        // Remove the product from the cart
        return currentCartItems.filter((item) => item.id !== product.id);
      } else {
        // Add the product to the cart
        return [...currentCartItems, product];
      }
    });
  };



  return (
    <div className="grid grid-cols-1 gap-6">
        {products.map((product) => (
          <ProductCard
            key={product.id}
            {...product}
            onAddToCart={() => handleAddOrRemoveFromCart(product)}
            isInCart={cartItems.some((item) => item.id === product.id)}
          />
        ))}
      </div>
  );
};

export default ProductList;
