import React, { useState } from 'react';

const AdminPage = () => {
  // Initial state for the form
  const initialFormState = {
    ProductId: null,
    Name: '',
    Description: '',
    Price: '',
    Category: '',
    ImageUrl: '',
    StockQuantity: '',
    Location: ''
  };
  
  const [products, setProducts] = useState([]); // Products list
  const [currentProduct, setCurrentProduct] = useState(initialFormState); // Currently selected product for edit
  const [isEditing, setIsEditing] = useState(false); // Is an edit operation ongoing

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setCurrentProduct({ ...currentProduct, [name]: value });
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    if (isEditing) {
      setProducts(products.map((product) => (product.ProductId === currentProduct.ProductId ? currentProduct : product)));
    } else {
      setProducts([...products, { ...currentProduct, ProductId: Date.now() }]);
    }
    setCurrentProduct(initialFormState);
    setIsEditing(false);
  };

  const handleEdit = (product) => {
    setCurrentProduct(product);
    setIsEditing(true);
  };

  const handleDelete = (productId) => {
    setProducts(products.filter(product => product.ProductId !== productId));
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-8">Admin - Product Management</h1>
      
      {/* Form for adding/editing a product */}
      <form onSubmit={handleFormSubmit} className="mb-8">
        {/* Product Name */}
        <div className="mb-4">
            <label htmlFor="name" className="block mb-2 text-sm font-medium text-gray-900">Product Name</label>
            <input type="text" id="name" name="Name" value={currentProduct.Name} onChange={handleInputChange} className="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5" required />
        </div>

        {/* Product Description */}
        <div className="mb-4">
            <label htmlFor="description" className="block mb-2 text-sm font-medium text-gray-900">Description</label>
            <textarea id="description" name="Description" value={currentProduct.Description} onChange={handleInputChange} className="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5" required />
        </div>

        {/* Product Price */}
        <div className="mb-4">
            <label htmlFor="price" className="block mb-2 text-sm font-medium text-gray-900">Price</label>
            <input type="number" id="price" name="Price" value={currentProduct.Price} onChange={handleInputChange} className="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5" required />
        </div>

        {/* Product Category */}
        <div className="mb-4">
            <label htmlFor="category" className="block mb-2 text-sm font-medium text-gray-900">Category</label>
            <input type="text" id="category" name="Category" value={currentProduct.Category} onChange={handleInputChange} className="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5" required />
        </div>

        {/* Product Image URL */}
        <div className="mb-4">
            <label htmlFor="imageUrl" className="block mb-2 text-sm font-medium text-gray-900">Image URL</label>
            <input type="text" id="imageUrl" name="ImageUrl" value={currentProduct.ImageUrl} onChange={handleInputChange} className="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5" required />
        </div>

        {/* Product Stock Quantity */}
        <div className="mb-4">
            <label htmlFor="stockQuantity" className="block mb-2 text-sm font-medium text-gray-900">Stock Quantity</label>
            <input type="number" id="stockQuantity" name="StockQuantity" value={currentProduct.StockQuantity} onChange={handleInputChange} className="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5" required />
        </div>

        {/* Product Location */}
        <div className="mb-4">
            <label htmlFor="location" className="block mb-2 text-sm font-medium text-gray-900">Location</label>
            <input type="text" id="location" name="Location" value={currentProduct.Location} onChange={handleInputChange} className="bg-gray-50 border border-gray-300 text-gray-900 sm:text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5" required />
        </div>

        {/* Submit Button */}
        <button type="submit" className="text-white bg-blue-600 hover:bg-blue-700 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center">
            {isEditing ? 'Update Product' : 'Add Product'}
        </button>
        </form>
    <div>
        {products.map((product) => (
          <div key={product.ProductId} className="flex justify-between items-center bg-white p-4 rounded-lg mb-4 shadow">
            <div>
              <h3 className="text-xl font-semibold">{product.Name}</h3>
                <div className="text-gray-500">{product.Description}</div>
                <div className="text-gray-500">{product.Price}</div>
                <div className="text-gray-500">{product.Category}</div>
            </div>
            <div>
              <button onClick={() => handleDelete(product.ProductId)} className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-700">
                Delete
              </button>
            </div>
            <div>
              <button onClick={() => handleEdit(product.ProductId)} className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-700">
                Edit
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminPage;
