import React, { useState, useEffect, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import ProductList from './components/ProductList';
import { useNavigate } from 'react-router-dom';
import Header from './components/Header';


const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [cartVisible, setCartVisible] = useState(false);
  const [cartItems, setCartItems] = useState([]);
  const [showUserDropdown, setShowUserDropdown] = useState(false);
  const userDropdownRef = useRef(null);
  const cartDropdownRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    function handleClickOutside(event) {
      if (userDropdownRef.current && !userDropdownRef.current.contains(event.target)) {
        setShowUserDropdown(false);
      }
      if (cartDropdownRef.current && !cartDropdownRef.current.contains(event.target)) {
        setCartVisible(false);
      }
    }

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleUserIconClick = () => {
    setShowUserDropdown(!showUserDropdown);
  };

  const handleCartIconClick = () => {
    setCartVisible(!cartVisible);
  };

  const handleLogin = () => {
    navigate('/login');
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setShowUserDropdown(false);
  };

  const handleCheckout = () => {
    navigate('/checkout', { state: { cartItems: cartItems } });
  };

  const handleAddToCart = (product) => {
    setCartItems(prevItems => {
      const isProductInCart = prevItems.find(item => item.id === product.id);
      if (!isProductInCart) {
        return [...prevItems, product];
      }
      return prevItems;
    });
  };

  const handleRemoveFromCart = (productId) => {
    setCartItems(prevItems => prevItems.filter(item => item.id !== productId));
  };


  return (
    <div className="App">
      <Header
        isLoggedIn={isLoggedIn}
        handleUserIconClick={() => setShowUserDropdown(!showUserDropdown)}
        handleCartIconClick={() => setCartVisible(!cartVisible)}
        cartItems={cartItems}
        handleCheckout={() => navigate('/checkout', { state: { cartItems: cartItems } })}
        handleLogout={() => {
          setIsLoggedIn(false);
          setShowUserDropdown(false);
        }}
        handleLogin={() => navigate('/login')}
      />
      <ProductList
        cartItems={cartItems}
        onAddToCart={handleAddToCart}
        onRemoveFromCart={handleRemoveFromCart}
      />
    </div>
  );
};

export default App
