import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartShopping } from '@fortawesome/free-solid-svg-icons';

const ProductCard = ({ imageUrl, alt, category, title, price, time, shipping, onAddToCart }) => {
  return (
    <div className="bg-white p-4 flex items-center shadow rounded-md">
      <img src={imageUrl} alt={alt} className="w-24 h-24 mr-4" />
      <div className="flex-grow">
        <div className="text-sm text-gray-600">{category}</div>
        <div className="text-lg font-medium">{title}</div>
        <div className="text-xl font-bold">{price}</div>
        <div className="text-sm text-gray-500">{time}</div>
      </div>
      <div className="text-right">
        {shipping && (
          <span className="bg-blue-100 text-blue-800 text-xs font-semibold px-2.5 py-0.5 rounded-full mb-2">
