// CheckoutPage.js
import React, {useContext} from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { CurrencyContext } from './context/CurrencyContext';

const CheckoutPage = () => {

  const { currency, exchangeRates } = useContext(CurrencyContext);
  const location = useLocation();
  const navigate = useNavigate();

  // Safeguard against undefined cartItems
  const cartItems = location.state?.cartItems ?? [];

  const convertPrice = (price, currency, exchangeRates) => {
    const cleanPrice = parseFloat(price.replace(/[^\d.]/g, '')); // Remove non-numeric chars except dot
    if (isNaN(cleanPrice)) {
      console.error('Invalid price:', price);
      return 'Invalid price'; // Handle invalid price gracefully
    }
  
    const rate = exchangeRates[currency];
    if (rate) {
      return (cleanPrice * rate).toFixed(2) + ` ${currency}`;
    } else {
      console.error('Exchange rate not found for currency:', currency);
      return cleanPrice.toFixed(2) + ' SEK'; // Fallback to SEK if rate is not found
    }
  };
  

  const calculateTotal = (items) => {
    return items.reduce((total, item) => {
      return total + parseFloat(convertedPrice(item.price));
    }, 0).toFixed(2);
  };
  
  const handleCheckout = () => {
    console.log('Handling checkout...');
    navigate('/success');
  };

  const navigateHome = () => {
    navigate('/'); // Navigate to the home page
  };

  return (
    <>
      <div className="bg-gradient-to-r from-red-500 to-yellow-500 to-green-500 to-blue-500 to-indigo-500 to-purple-500 h-1"></div>
      <header className="bg-white py-4 flex justify-between items-center">
        <div className="container mx-auto px-4 flex justify-between items-center">
          <h1 className="text-3xl font-bold text-red-600 cursor-pointer" onClick={navigateHome}>blocket</h1>
          <div className="flex items-center">
            {/* Other header icons or elements can go here */}
          </div>
        </div>
      </header>
      <div className="container mx-auto p-4">
        <h2 className="text-2xl font-bold mb-4">Checkout</h2>
        {cartItems.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <>
            <ul className="mb-4">
            {cartItems.map((item) => (
                <li key={item.id} className="flex justify-between items-center mb-2">
                    <span>{item.title}</span>
                    <span>{convertPrice(item.price)} {currency}</span>
                </li>
            ))}
            </ul>
            <div className="flex justify-between items-center font-bold mb-4">
              <span>Total:</span>
              <span>Total: {calculateTotal(cartItems)} {currency}</span>
            </div>
            <button
              onClick={handleCheckout}
              className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-700 transition-colors"
              disabled={cartItems.length === 0}
            >
              Confirm and Pay
            </button>
          </>
        )}
      </div>
    </>
  );
};

export default CheckoutPage;
